import React, {useEffect, useState}from "react";
import axios from "axios";
import { Container } from "@material-ui/core";
import ImageGallary from "./component/imageGallary";
import Search from "./component/search";
import { CircularProgress } from "@material-ui/core";
import { Pagination } from "@material-ui/lab";
const App = () =>{
  const [photos, setPhotos] = useState([])
  const [loading, setLoading] = useState(true);
  const [formData, setFormData]= useState('');

  const fetchData = async(input) =>{
    setLoading(true)
    input.preventDefault()
    const response= await axios.get(
      'https://api.unsplash.com/search/photos?page=1&query=${input}&client_id=SPInJO4rdl9Rv1lTehjZX-m6Wg8A5VaBnSFvwKJ7Y-I&per_page=16'
      );
    const data = await response.data
    setPhotos(data);
    console.log(data);
    setLoading(false);
  }
  const handleChange =(event)=>{
    setFormData(event.target.value)
  }
  const handleSubmit =(event)=>{
    event.preventDefault();
    fetchData(formData);
  }
  useEffect(() =>{
    fetchData();
  },[]);

  if(loading)
   return <div style={
    {
      height:'100vh',
      display:'flex',
      justifyContent:'center',
      alignItems:'center',
    }
  }
  ><CircularProgress size={150}/>
  </div>

  return(
    <Container>
      <Search change={handleChange} submit={handleSubmit}/>
      <ImageGallary photos={photos.results}/>
      <Pagination/>
    </Container>
  )
}



export default App
