import { CircularProgress } from "@material-ui/core";

const App = () =>{
  const [photos, setPhotos] = useState([])
  const [loading, setLoading] = useState(true);

  const fetchData = async() =>{
    setLoading(true)
    const response= await axios.get(
      'https://api.unsplash.com/search/photos?page=1&query=${e}&client_id=SPInJO4rdl9Rv1lTehjZX-m6Wg8A5VaBnSFvwKJ7Y-I'
      );
    const data = await response.data
    console.log(data)
    setLoading(true);
  }
  useEffect(() =>{
    fetchData();
  },[]);

  if(loading) return <div style={
    {
      height:'100vh',
      display:'flex',
      justifyContent:'center',
      alignItems:'center',
    }
  }
  ><CircularProgress size={150}/>
  </div>
  return(
    <Container>
      <Search />
      <ImageGallary />
    </Container>
  )
}



export default App
